mapper.select:
        SELECT


--         级联表的字段

        FROM qicai  a



                and a.id in

                #{item}



                and a.qicai_name like CONCAT('%',#{params.qicaiName},'%')


                and a.qicai_types = #{params.qicaiTypes}


                  and a.qicai_number >= #{params.qicaiNumberStart}   


                  and a.qicai_number <= #{params.qicaiNumberEnd}   


                and a.qicai_number = #{params.qicaiNumber}


                and a.qicai_content like CONCAT('%',#{params.qicaiContent},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
